import React, { Component } from 'react';

import Modal from 'react-bootstrap/Modal';
import { AiFillCheckCircle } from 'react-icons/ai';
import { IconContext } from 'react-icons';
import css from '../../scss/modalStyle.scss';
import Button from 'react-bootstrap/Button';

class Feedback extends Component {
    render() {
        return (
            <Modal
                show={this.props.showModal}
                onHide={this.props.handleHide}
                centered
                className={css.modal} // style more later including backdrop/background
            >
                <Modal.Dialog className={css.modalConfirmDialog}>
                    <Modal.Body className={css.modalContent}>
                        <Modal.Body className={css.modalHeader}>
                             <IconContext.Provider
                                 value={{ size: '70px', className: 'global-class-name' }}
                             >
                                 <div>
                                     <AiFillCheckCircle />
                                 </div>
                             </IconContext.Provider>
                            <h4 className="mx-auto">Congratulations!</h4>
                        </Modal.Body>
                        <Modal.Body className={css.body}>
                            <p className="text-center">That was the correct answer.</p>
                        </Modal.Body>
                        <Modal.Body>
                            <Button
                                className="btn btn-success btn-block"
                                data-dismiss="modal"
                                onClick={this.props.handleHide}
                            >
                                OK
                            </Button>
                        </Modal.Body>
                    </Modal.Body>
                </Modal.Dialog>
            </Modal>
        );
    }
}

export default Feedback;
