import React, { Component } from 'react';
import Table from "react-bootstrap/Table";
import SampleQ from '../components/question.js';
import css from '../scss/style.scss';
import Layout from '../components/layout';
import QuestionTable from "../components/table";

export default class extends Component {
    constructor(props) {
        super(props);

        this.state = {
            showBackground: false
        };

        this.showBackground = () => {
            this.setState({ showBackground: true });
        };

        this.hideBackground = () => {
            this.setState({ showBackground: false });
        }
    }

    static getInitialProps({query: {categories, questions, answers}}) {
        return {postCs: categories, allQs: questions, allAs: answers}
    }

    render() {
        return (
            <div>
                {this.state.showBackground ?
                    <Layout showBackground={this.state.showBackground} title="lllll" />
                :
                    <QuestionTable cats={this.props.categories} qs={this.props.questions} ans={this.props.answers}/>
                }
            </div>
        )
    }
}
