import React, { Component } from 'react';

import Modal from 'react-bootstrap/Modal';
import { AiFillCheckCircle } from 'react-icons/ai';
import { IconContext } from 'react-icons';
import css from '../../scss/modalStyle.scss';
import Button from 'react-bootstrap/Button';
import Backdrop from '../../components/backdrop';

class Feedback extends Component {
    constructor(props) {
        super(props);

        this.state = {
            showBackdrop: true
        };

        this.hideBack = () => {
            this.setState({ showBackdrop: false });
            this.props.handleHide();
        };
    }

    render() {
        let backdrop;

        return (
            <Modal
                show={this.props.showModal}
                onHide={this.props.handleHide}
            >
                {/*<Backdrop showBack={this.state.showBackdrop} />*/}
                <Modal.Dialog className={css.modalConfirm}>
                    <Modal.Body className={css.modalContent}>
                        <Modal.Body className={css.modalHeader}>
                             <IconContext.Provider
                                 value={{
                                     color: '#fff',
                                     size: '70px',
                                     className: css.modalConfirmIconBox,
                                 }}
                             >
                                 <div>
                                     <AiFillCheckCircle />
                                 </div>
                             </IconContext.Provider>
                            <h4 className="mx-auto">Congratulations!</h4>
                        </Modal.Body>
                        <Modal.Body className={css.body}>
                            <p className="text-center">That was the correct answer.</p>
                        </Modal.Body>
                        <Modal.Body className={css.modalFooter}>
                            <Button
                                className="btn btn-success btn-block"
                                data-dismiss="modal"
                                onClick={this.hideBack}
                            >
                                OK
                            </Button>
                        </Modal.Body>
                    </Modal.Body>
                </Modal.Dialog>
            </Modal>
        );
    }
}

export default Feedback;
