import React, { Component } from 'react';

import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';

class UserInput extends Component {
    constructor(props) {
        super(props);

        // this.handleChange = (event) => {
        //     this.props.handleInput(event.target.value);
        // }

        this.handleLog = (event) => {
            console.log(event.currentTarget);
            console.log("OIWEGO");
        }
    }

    render() {
        return (
            <Form onSubmit={this.handleLog()}> /* should be this.props.handleClick*/
                <Form.Group as={Row} controlId="formAnswer">
                    <Form.Label column sm={2}>
                        Answer
                    </Form.Label>
                    <Col sm={10}>
                        <Form.Control type="text" placeholder="Enter Here"  />
                    </Col>
                </Form.Group>
                <div>
                    <Button variant="primary" onClick={this.props.handleClick}>
                        Submit
                    </Button>
                </div>
            </Form>
        );
    }
}

export default UserInput;

